<?php// File: includes/class-btr-metabox.phpif ( ! defined( 'ABSPATH' ) ) {    exit; // Impedisce l'accesso diretto al file}class BTR_Metabox {    public function __construct() {        // Hook per aggiungere i metabox        // Nota: Questo è già gestito nella classe BTR_Pacchetti_CPT, quindi potrebbe essere ridondante.        // Tuttavia, se desideri una separazione più chiara, puoi spostare la logica dei metabox qui.        // Hook per enqueue gli script e gli stili specifici del metabox        //add_action( 'admin_enqueue_scripts', array( $this, 'enqueue_metabox_assets' ) );    }    /**     * Carica gli script e gli stili necessari per il metabox     */    public function enqueue_metabox_assets() {        global $post;        if ( ! $post || 'btr_pacchetti' !== $post->post_type ) {            return;        }        // Carica script e stili personalizzati per il metabox       // wp_enqueue_style( 'btr-metabox-styles', BTR_PLUGIN_URL . 'assets/css/metabox-styles.css', array(), '1.0.0' );        //wp_enqueue_script( 'btr-metabox-scripts', BTR_PLUGIN_URL . 'assets/js/metabox-scripts.js', array( 'jquery' ), '1.0.0', true );        // Passa variabili PHP a JavaScript        wp_localize_script( 'btr-metabox-scripts', 'btr_metabox_object', array(            'nonce' => wp_create_nonce( 'btr_metabox_nonce' ),            // Altre variabili possono essere aggiunte qui        ) );    }    /**     * Esempio di funzione per sanitizzare una data range     * Potresti avere più funzioni di sanitizzazione per altri campi complessi     */    public function sanitize_date_range( $range ) {        $sanitized = array();        $sanitized['start'] = isset( $range['start'] ) ? sanitize_text_field( $range['start'] ) : '';        $sanitized['end']   = isset( $range['end'] ) ? sanitize_text_field( $range['end'] ) : '';        return $sanitized;    }    // Aggiungi altre funzioni di sanitizzazione se necessario}