<?php/** * Template per il modulo di richiesta preventivo. */?><form action="" method="post" class="btr-preventivo-form">    <?php wp_nonce_field( 'btr_submit_preventivo', 'btr_preventivo_nonce_field' ); ?>    <p>        <label for="btr_cliente_nome"><?php _e( 'Nome:', 'born-to-ride-booking' ); ?></label><br>        <input type="text" id="btr_cliente_nome" name="btr_cliente_nome" required />    </p>    <p>        <label for="btr_cliente_email"><?php _e( 'Email:', 'born-to-ride-booking' ); ?></label><br>        <input type="email" id="btr_cliente_email" name="btr_cliente_email" required />    </p>    <p>        <label for="btr_cliente_telefono"><?php _e( 'Telefono:', 'born-to-ride-booking' ); ?></label><br>        <input type="text" id="btr_cliente_telefono" name="btr_cliente_telefono" />    </p>    <p>        <label for="btr_pacchetto_id"><?php _e( 'Seleziona Pacchetto:', 'born-to-ride-booking' ); ?></label><br>        <select id="btr_pacchetto_id" name="btr_pacchetto_id" required>            <option value=""><?php _e( 'Seleziona un Pacchetto', 'born-to-ride-booking' ); ?></option>            <?php            $pacchetti = get_posts( array(                'post_type'      => 'btr_pacchetti',                'posts_per_page' => -1,                'post_status'    => 'publish',            ) );            foreach ( $pacchetti as $pacchetto ) {                echo '<option value="' . esc_attr( $pacchetto->ID ) . '">' . esc_html( get_the_title( $pacchetto->ID ) ) . '</option>';            }            ?>        </select>    </p>    <p>        <input type="submit" name="btr_submit_preventivo" value="<?php _e( 'Richiedi Preventivo', 'born-to-ride-booking' ); ?>" />    </p></form>